import React , { useState } from 'react';

const ResetPassword = () => {
    const [email , setEmail] = useState('');

    const onChange = e => setEmail(e.target.value);
    return (
        <div className="container">
            <div className="row">
                <div className="card col-12 mt-4" style={{padding:'20px'}}>
                    <form>
                        <div className="form-group">
                            <label htmlFor="email">Email : </label>
                            <input type="email" required name="email" className="form-control" value={email} onChange={onChange} />
                        </div>
                        <input type="submit" className="btn btn-outline-success text-center" value="Check my email" />
                    </form>
                </div>
            </div>
        </div>
    )
}

export default ResetPassword
