import React , { useState } from 'react';

const EditPostForm = ({oldPost}) => {
    
    const { post_id , post_cat_id , post_title , post_discription , post_status } = oldPost;

    const [updatePost , setUpdatePost] = useState({
        update_title : `x`,
        discription : '',
        status : '',
        cat_id : 1,   
    });
    
    const { update_title } = updatePost;

    const onChange = e => setUpdatePost({...updatePost , [e.target.name]:e.target.value});

    const onUpdatePost = (e) => {
        e.preventDefault();
        console.log(updatePost);
    }

    return (
        <div className="card mt-4" style={{padding:'20px'}}>
            <form onSubmit={onUpdatePost}>

                <div className="form-group">
                    <label htmlFor="title">Post title : </label>
                    <input type="text" required className="form-control" name="update_title" value={update_title} onChange={onChange} />
                </div>
            </form>
            
        </div>
    )
}

export default EditPostForm
