import { REGISTER_USER , UPDATE_USER , DELETE_USER , LOGIN , LOGOUT , LOGIN_FAIL } from '../type';

export default (state , action) =>{
    switch(action.type) {
        case REGISTER_USER :
            return {
                ...state,
                message : action.payload.message ,
                isAuthenticated : true
            };
         case LOGIN : 
            return {
                ...state,
                singleUser : action.payload , 
               isAuthenticated : true
            }  ;

        
        default : 
            return state;
    }
}
