import { GET_COOKIES , REMOVE_COOKIES } from '../type';

export default (state , action) => {
    switch(action.type) {
        case GET_COOKIES : 
            return {
                ...state,
                user_id : action.payload.user_id,
                cookieUserName : action.payload.userName , 
                cookiePassword : action.payload.password
            }
        case REMOVE_COOKIES : 
            return {
                ...state , 
                user_id : '',
                cookieUserName : '', 
                cookiePassword : ''
            }
        default : 
            return state;
    }
}
