import React , { useEffect  , useState } from 'react';

const EditPostForm = ({oldPost}) => {
    
    const [post , setPost] = useState(JSON.parse(localStorage.getItem('currentPost')));

    useEffect(()=>{
        console.log(post);
    },[]);

    const onChange = e => {};

    const onUpdatePost = (e) => {
        e.preventDefault();
       
    }

    return (
        <div className="card mt-4" style={{padding:'20px'}}>
            <form onSubmit={onUpdatePost}>

                <div className="form-group">
                    <label htmlFor="title">Post title : </label>
                    <input type="text" required className="form-control" name="update_title" onChange={onChange} />
                </div>
            </form>
            
        </div>
    )
}

export default EditPostForm
