    const [oldsPost , setOldsPost] = useState({});

    const getPostById = async(call) => {
        const oldPostRes = await axios.get(`http://localhost/react_cms/api/posts/specialPost.php?post_id=${post_id}`);

        call(data=oldPostRes.data)
    }

    useEffect(()=> {
        getPostById((data) => console.log(data));
        console.log(oldsPost);
    }, []);
    return (
        <div>
          
        </div>
    )
}

export default EditPost
