import axios from 'axios';
import UserContext from './userContext';
import userReducer from './userReducer';
import {
  REGISTER_USER , 
  LOGIN 
} from '../type';

const UserState = props => {
  const initialState = {
    firstName : '',
    lastName : '',
    email : '',
    role : '',
    userName : '',
    password : ''
  };

  const [state, dispatch] = useReducer(userReducer, initialState);

