    <UserContext.Provider 
    value={{
        firstName : state.firstName,
        lastName : state.lastName,
        email : state.email,
        userName : state.userName , 
        password : state.password,
        role : state.role,
        registerUser
    }}>
    {props.children}
    </UserContext.Provider>
)

 
};

export default UserState;
