import React , { useState  , useEffect } from 'react';
import CategoryItem from './layout/CategoryItem';
import axios from 'axios';

const Categories = () => {
    const [categories , setCategories] = useState([{
        'id' :'1' ,
        'title' : 'salam',
        'discription' : 'dfdsf'
    }]);

    const getCatData = async () => {
      const catDat = await  axios.get('http://localhost/react_cms/api/categories/get.php');
      setCategories(catDat.data);
    }

    useEffect(()=>{
        getCatData();
    } , []);

    const onClick = e =>{
      if(e.target.classList.contains('my_items')) {
        const itemValue = e.target.parentNode.value;
        
        const getIdArray = itemValue.split('_');
        const getId = getIdArray[0];
        console.log(getId);
      }
    }

    return (
        <select className="form-control cat_list" onClick={onClick}>
            {
                categories.map(category => (
                    <CategoryItem key={category.id} category={category} />
                    ))
            }
        </select>
    )
}

export default Categories
