import React , { useEffect , useContext , useState } from 'react';
import cookies from 'js-cookie';
import UserContext from '../../context/user/userContext';
import axios from 'axios';

const UserPost = (props) => {
    const userContext = useContext(UserContext);
    const { userLogIn } = userContext;

    const [usersPost , setUsersPost] = useState([]);

    if(cookies.get('userName')==null) {
        window.location.href="/logIn";
    }

    const userData = cookies.getJSON('user_data');

    const searchUserPost = async () => {
        const postResponse = await axios.get(`http://localhost/react_cms/api/posts/usersPost.php?user_id=${userData.user_id}
            `); 

         setUsersPost(postResponse.data);   
        console.log(postResponse.data);    
    }

    useEffect(()=>{
        if(cookies.get('userName')==null) {
            props.history.push('/logIn');
        } else {
            userLogIn({
                userName:cookies.get('userName'),
                password:cookies.get('password')
            });

            searchUserPost();
            
        }
        // eslint-disable-next-line
    },[props.history]);
    

    return (
        <div className="container">
            {usersPost.map(post=>(<p>{post.title}</p>))}
        </div>
    )
}

export default UserPost
