    const [ownPost , setOwnPost] = useState([
        {
            'id' : 1,
            'title' : 'new post'
        }
    ]);

    if(cookies.get('userName')==null) {
        window.location.href="/logIn";
    }

    const userData = cookies.getJSON('user_data');

    const searchUserPost = async (callBack) => {
        const ownUserResponse = await axios.get(`http://localhost/react_cms/api/posts/usersPost.php?
             user_id=${userData.user_id}`); 

