import { REGISTER_USER , LOGIN , LOGIN_FAIL } from '../type';

export default (state , action) =>{
    switch(action.type) {
        case REGISTER_USER :
            return {
                ...state,
                message : action.payload.message ,
                isAuthenticated : true
            };
         case LOGIN : 
            return {
                ...state,
                singleUser : action.payload , 
               isAuthenticated : true
            }   
        case LOGIN_FAIL : 
            return {
                ...state , 
                singleUser : null , 
                isAuthenticated : false
            }
        default : 
            return state;
    }
}
