import React , { useReducer } from 'react';
import axios from 'axios';
import CookieContext from './cookieContext';
import CookieReducer from './cookieReducer';
import { GET_COOKIES , REMOVE_COOKIES } from '../type';

const CookieState = props => {
    const inisialState = {
        user_id : '',
        cookieUserName : '',
        cookiePassword : ''
    };
    const [state , dispatch] = useReducer(CookieReducer , inisialState);

    //Get cookies
    const getCookies = async () => {
        const response = await axios.get('http://localhost/react_cms/api/cookies/get.php');

        dispatch({type : GET_COOKIES , payload : response.data});
    }

    //Remove cookies
    const removeCookies = () =>{
        dispatch({type:REMOVE_COOKIES});
    }
    return (
        <CookieContext.Provider value={{
           user_id : state.user_id,
           cookieUserName : state.cookieUserName,
           cookiePassword : state.cookiePassword,
           getCookies,
           removeCookies
        }}>
            {props.children}
        </CookieContext.Provider>
    )
}

export default CookieState
