import React , { useState  , useContext , useEffect } from 'react';
import Categories from '../Categories';
import CotegoryContext from '../../context/category/categoryContext';


const PostForm = ({ user_id }) => {
    const [post , setPost] = useState({
        title : '',
        discription : '',
        status : '',
        cat_id : 0,
        user_id : user_id
    });

    const { title , discription , status } = post;
    const onChange = e => setPost({...post , [e.target.name]:e.target.value});

    const categoryContext = useContext(CotegoryContext);
    const { getCategories , category }=categoryContext;

    useEffect(()=>{
        getCategories()

    } , []);
    
    const cats = [
        {
            'id' : 1,
            'title' : 'new'
        }
    ]

    return (
        <div className="card" style={{padding:'15px'}}>
            <form>
                <div className="form-group">
                    <label for="title">Post title : </label>
                    <input type="text" required className="form-control" name="title" value={ title } onChange={ onChange } />
                </div>

                <div className="form-group">
                    <label for="discription">Post discription : </label>
                    <textarea required className="form-control" value={ discription } onChange={ onChange } name="discription"></textarea>
                </div>

                <dv className="form-group">
                    
                    <Categories categories={cats}/>
                </dv>
                <br/>

                <input type="submit" value="Save post" className="btn btn-outline-warning" style={{width:'100%'}} />
            </form>
        </div>
    )
}

export default PostForm
