import React , { useState } from 'react';
import cookies from 'js-cookie';


const ResetPassword = () => {

    if(cookies.get('userName')==null) {
        window.location.href="/logIn";
    }

    const [email , setEmail] = useState('');

    const onChange = e => setEmail(e.target.value);

    const onSubmit = e => {
        e.preventDefault();
       
    }

    return (
        <div className="container">
            <div className="row">
                <div className="card col-12 mt-4" style={{padding:'30px'}}>
                    <form onSubmit={onSubmit}>
                        <div className="form-group">
                            <label htmlFor="email">Email : </label>
                            <input type="email" required name="email" className="form-control" value={email} onChange={onChange} />
                        </div>
                        <input type="submit" style={{width:'100%'}} className="btn btn-outline-success text-center" value="Check my email" />
                    </form>
                </div>
            </div>
            <div className="row">
                <div className="card col-12 mt-4" style={{padding:'30px'}}>
                    dsfdsf
                </div>
            </div>
        </div>
    )
}

export default ResetPassword
