import React , { Fragment ,useContext  , useState}  from 'react';
import Nav from '../layout/Nav';
import Register from './RegisterUser';
import Alert from '../layout/Alert';
import CookieContext from '../../context/cookies/cookieContext';

const Home = () => {
    const userCookies = useContext(CookieContext);
    const {user_id , cookieUserName , cookiePassword} = userCookies;

    const [userLoginForm , setUserLoginForm] = useState('')
    const cheeckCookies = () => {
        if(cookieUserName === '' && cookiePassword === '') {
            setUserLoginForm(<Register />);
        } 
    }
    return (
        <Fragment>
            <Nav />
            <br/><br /><br/>
            <Alert />
            <br/><br /><br/>
            <div className="container">
            {cheeckCookies}
            {userLoginForm}
            </div>
        </Fragment>
    )
}

export default Home
