import React, { useReducer } from 'react';
import axios from 'axios';
import UserContext from './userContext';
import userReducer from './userReducer';
import { REGISTER_USER , UPDATE_USER , DELETE_USER , LOGIN , LOGOUT } from '../type';

const userState = (props) => {

    const initialState = {
        firstName : '',
        lastName : '',
        email : '',
        userName : '',
        password : '',
        role : 'typical'
    };
    
    const [state , dispatch] = userReduser(userReduser , initialState);
    //Register user
    const registerUser = async userData =>{
        const config = {
            header : {
                'Content-Type' : 'application/json'
            }
        };
        try {
            const response = await Axios.post('http://localhost/react_cms/api/users/register.php' , userData , config);
            dispatch({type : REGISTER_USER , payload : response});
        } catch (error) {
            console.log(error.response);
        }
    }

    return (
        <UserContext.Provider 
        value={{
            firstName : state.firstName,
            lastName : state.lastName,
            email : state.email,
            userName : state.userName , 
            role : state.role,
            registerUser
        }}>
        {props.children}
        </UserContext.Provider>
    )
};

export default userState
